import React, { useState, useEffect } from 'react';

function Quiz() {
  const [randomSign, setRandomSign] = useState(null);

  const generateRandomSign = () => {
    const signs = ['A', 'B', 'C', 'D', 'E']; // Add more signs as needed
    const randomIndex = Math.floor(Math.random() * signs.length);
    return signs[randomIndex];
  };

  useEffect(() => {
    setRandomSign(generateRandomSign());
  }, []);

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">Quiz</h2>
      {randomSign && (
        <div className="hand-sign text-center" data-sign={randomSign}>
          <img src={`${process.env.PUBLIC_URL}/hand_signs/${randomSign}.png`} alt={`${randomSign} Hand Sign`} />
          <p>{randomSign}</p>
        </div>
      )}
    </div>
  );
}

export default Quiz;
