import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import '../index.css'; // Import the index.css file which includes Tailwind CSS
import Lesson from './Lesson';
import Quiz from './Quiz';

function App() {
  return (
    <Router>
      <div className="App">
        <h1 className="text-3xl font-bold mb-4">Learnix: Sign Language Recognition</h1>
        <nav>
          <ul className="flex space-x-4">
            <li>
              <Link to="/lesson" className="text-blue-500 hover:underline">Take Lesson</Link>
            </li>
            <li>
              <Link to="/quiz" className="text-blue-500 hover:underline">Take Quiz</Link>
            </li>
          </ul>
        </nav>
        <Routes>
          <Route path="/lesson" element={<Lesson />} />
          <Route path="/quiz" element={<Quiz />} />
          <Route path="/" element={<h2 className="text-xl">Welcome to Learnix</h2>} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
