import React, { useState } from 'react';
import HandSign from './HandSign';

const Lesson = () => {
    const [isLessonVisible, setIsLessonVisible] = useState(false);

    const handSigns = [
        { letter: 'A', src: '/hand_signs/A.png' },
        { letter: 'B', src: '/hand_signs/B.png' },
        { letter: 'C', src: '/hand_signs/C.png' },
        { letter: 'D', src: '/hand_signs/D.png' },
        { letter: 'E', src: '/hand_signs/E.png' },
        { letter: 'F', src: '/hand_signs/F.png' },
        { letter: 'G', src: '/hand_signs/G.png' },
        { letter: 'H', src: '/hand_signs/H.png' },
        { letter: 'I', src: '/hand_signs/I.png' },
        { letter: 'J', src: '/hand_signs/J.png' },
        { letter: 'K', src: '/hand_signs/K.png' },
        { letter: 'L', src: '/hand_signs/L.png' },
        { letter: 'M', src: '/hand_signs/M.png' },
        { letter: 'N', src: '/hand_signs/N.png' },
        { letter: 'O', src: '/hand_signs/O.png' },
        { letter: 'P', src: '/hand_signs/P.png' },
        { letter: 'Q', src: '/hand_signs/Q.png' },
        { letter: 'R', src: '/hand_signs/R.png' },
        { letter: 'S', src: '/hand_signs/S.png' },
        { letter: 'T', src: '/hand_signs/T.png' },
        { letter: 'U', src: '/hand_signs/U.png' },
        { letter: 'V', src: '/hand_signs/V.png' },
        { letter: 'W', src: '/hand_signs/W.png' },
        { letter: 'X', src: '/hand_signs/X.png' },
        { letter: 'Y', src: '/hand_signs/Y.png' },
        { letter: 'Z', src: '/hand_signs/Z.png' },
    ];

    return (
        <div>
            <h2>Learn the Alphabet</h2>
            {isLessonVisible && (
                <div className="hand-sign-grid">
                    {handSigns.map((handSign) => (
                        <HandSign key={handSign.letter} letter={handSign.letter} src={handSign.src} />
                    ))}
                </div>
            )}
            <button onClick={() => setIsLessonVisible(!isLessonVisible)}>
                {isLessonVisible ? 'Hide Lesson' : 'Take Lesson'}
            </button>
            {isLessonVisible && <button onClick={() => window.location.href = '/quiz'}>Take Quiz</button>}
        </div>
    );
};

export default Lesson;
